{
    "schema": "https://schema.yaitde.com/json/collection/v0/collection.json",
    "description" : "UPFULL Collection",
    "tree": {
        "type": "f",
        "resourceName": "Upsert Full - All New Ids",
        "children": [
            {
                "resourceName": "token - UPFULL",
                "type": "x",
                "data" : {
                    "request": {
                        "url": "http://localhost:3000/auth",
                        "headers": [
                            {
                                "key": "Authorization",
                                "value": "Bearer SomeToken",
                                "type": "text"
                            },
                            {
                                "key": "Content-Type",
                                "value": "application/json",
                                "name": "Content-Type",
                                "type": "text"
                            }
                        ],
                        "method": "POST",
                        "body": {
                            "type": "raw",
                            "raw": "{\n    \"userId\": \"eo@yaitde.com\",\n    \"scope\": \"mock\",\n    \"secret\": \"6v9y$B&E)H@MbQeThWmZq4t7w!z%C*F-\",\n    \"instance\" : \"my-named-instance\"\n}"
                        }
                    },
                    "description": "UPFULL Test - Get A Token",
                    "scripts": [
                        {
                            "on": "test",
                            "data": "// UPFULL\npm.test(\"Status code is 200\", function () {\n    pm.response.to.have.status(200);\n});\n\nvar jsonData = pm.response.json();\npm.environment.set(\"token\", jsonData.token);\n",
                            "type": "js",
                            "disabled": false,
                            "description": "This is a script against the UPFULL token test"
                        }
                    ]
                }
            },
            {
                "resourceName": "UPFULL-echo - app1",
                "type": "x",
                "data" : {
                    "request": {
                        "url": "http://localhost:3000/api/echo",
                        "headers": [
                            {
                                "key": "Content-Type",
                                "value": "application/json",
                                "name": "Content-Type",
                                "type": "text"
                            },
                            {
                                "key": "x-yaitde-application",
                                "value": "yaitde-mock-master-67",
                                "type": "text"
                            }
                        ],
                        "method": "POST",
                        "body": {
                            "type": "raw",
                            "raw": "{\n\t\"test\" : \"json\",\n\t\"property1\" : 5\n}"
                        }
                    },
                    "description": "UPFULL Echo to  App1 (mock)",
                    "scripts": [
                        {
                            "on": "test",
                            "data": "//UPFULL\npm.test(\"Status code is 200\", function () {\n    pm.response.to.have.status(200);\n});\n",
                            "type": "js",
                            "disabled": false,
                            "description": "This is a script against the UPFULL echo test"
                        }
                    ]
                }
            }
        ]
    }
}

