							"pm.test(\"Status code is 200\", function () {",
							"    pm.response.to.have.status(200);",
							"});"
						],
						"type": "text/javascript"
					}
				}
			],
			"request": {
				"method": "POST",
				"header": [
					{
						"key": "Content-Type",
						"name": "Content-Type",
						"type": "text",
						"value": "application/json"
					},
					{
						"key": "x-yaitde-application",
						"type": "text",
						"value": "{{appId}}"
					}
				],
				"body": {
					"mode": "raw",
					"raw": "{\n\t\"test\" : \"json\",\n\t\"property1\" : 5\n}"
				},
				"url": {
					"raw": "{{url}}/api/echo",
					"host": [
						"{{url}}"
					],
					"path": [
						"api",
